import React, { useEffect, useState } from 'react'
import { List, Card, Badge } from 'antd'
import {
    StarOutlined,
    CalendarOutlined,
} from '@ant-design/icons';
import axios from 'axios'
import { API_URL } from '../Config'
import { Image } from 'antd';
import { useHistory, useParams } from 'react-router-dom';
import { Descriptions } from 'antd';
import { PageHeader } from 'antd';
import { Tag } from 'antd';

const GameDetail = (props) => {

    const [detailGame, setDetailGame] = useState(null)

    const history = useHistory()
    const { id, name } = useParams()
    const game_id = id
    console.log(id, name)

    useEffect(() => {
        if (detailGame === null) {
            // if (id === null) {
            //     history.push('/games')
            // }
            getDetailGame(game_id);
        }
    }, [detailGame, game_id])

    const getDetailGame = (id) => {
        axios.get(`${API_URL}/data-game/${id}`)
            .then((res) => {
                let mdata = res.data
                setDetailGame(mdata)
            }).catch((err) => {
                alert(`Maaf, kendala koneksi (Err: ${err})`)
            })
    }

    const getDuration = (minutes) => {
        let duration_h = Math.floor(minutes / 60)
        let duration_m = (minutes % 60) === 0 ? '' : (minutes % 60 + ' minute')
        return `${duration_h} hour ${duration_m}`
    }

    return (
        <>
            {
                detailGame !== null && (
                    <>
                        <PageHeader
                            className="site-page-header"
                            onBack={() => history.push('/games')}
                            title={detailGame.name}
                        // subTitle="This is a subtitle"
                        />
                        <Descriptions
                            // title={detailGame.name}
                            bordered
                            layout="horizontal"
                            column={{ xxl: 1, xl: 1, lg: 1, md: 1, sm: 1, xs: 1 }}
                        >
                            <Descriptions.Item label="">
                                <Image alt={detailGame.name} src={detailGame.image_url}
                                    fallback="data:image/png;base64,"
                                />
                            </Descriptions.Item>
                            <Descriptions.Item label="Release Year">{detailGame.release}</Descriptions.Item>
                            <Descriptions.Item label="Gameplay">{detailGame.singlePlayer === 1 ? 'Singleplayer' : 'Multiplayer'}</Descriptions.Item>
                            <Descriptions.Item label="Genre">{detailGame.genre}</Descriptions.Item>
                            <Descriptions.Item label="Platform">
                                {
                                    detailGame.platform.split(',').map((platf, idx) => {
                                        return (
                                            <Tag key={idx} color="success">{platf}</Tag>
                                        )
                                    })
                                }
                            </Descriptions.Item>
                        </Descriptions>
                    </>
                )
            }
        </>
    )
}

export default GameDetail