import React, { Component } from 'react'
import { List, Card, Badge } from 'antd'
import {
    PlayCircleTwoTone,
    CalendarOutlined,
} from '@ant-design/icons';
import axios from 'axios'
import { API_URL } from '../Config'
import { NavLink } from 'react-router-dom';
import { Image, Tag, Typography } from 'antd';

const { Title } = Typography
class Home extends Component {
    constructor(props) {
        super(props)
        this.state = {
            gameList: null,
        }
    }

    componentDidMount() {
        this.state.gameList === null && this.getGameList()
    }

    getGameList() {
        console.log('get game list')
        axios.get(`${API_URL}/data-game`)
            .then((res) => {
                let mdata = res.data
                mdata = mdata.sort(function (x, y) {
                    return y.rating - y.rating
                })
                this.setState({
                    gameList: mdata,
                })
            }).catch((err) => {
                alert(`Maaf, kendala koneksi (Err: ${err})`)
            })
    }

    handleImage(elm) {
        elm.target.onerror = null
        elm.target.src = ''
    }

    render() {
        let gameList = this.state.gameList
        const { Meta } = Card
        return (
            <>
                {
                    gameList !== null && (
                        <>
                            <Title level={2}>Daftar Game Game Terbaik</Title>
                            <List
                                grid={{
                                    gutter: 16,
                                    xs: 1,
                                    sm: 2,
                                    md: 4,
                                    lg: 4,
                                    xl: 4,
                                    xxl: 3,
                                }}
                                dataSource={gameList}
                                renderItem={(item, index) => (
                                    <>
                                        <List.Item key={index}>
                                            {/* {
                                        let duration_h = Math.floor(item.duration / 60);
                                        let duration_m = (item.duration % 60) === 0 ? '' : (item.duration % 60 + ' menit');
                                        let duration = `${duration_h} jam ${duration_m}`;
                                    } */}
                                            {/* <Badge.Ribbon text={(<Rate disabled defaultValue={item.rating / 2} />)}> */}
                                            <NavLink to={`/game/${item.id}/${item.name}`}>
                                                <Badge.Ribbon placement="start" color="red" text={(<><CalendarOutlined /> <b>{item.release}</b></>)}>
                                                    <Badge.Ribbon placement="end" color="gold" text={(<><PlayCircleTwoTone /> <b>{item.singlePlayer === 1 ? 'Singleplayer' : 'Multiplayer'}</b></>)}>
                                                        <Card key={index} hoverable cover={
                                                            <Image alt={item.name} src={item.image_url}
                                                                fallback="data:image/png;base64,"
                                                            />
                                                        }>
                                                            <Meta title={item.name} description={(
                                                                <>
                                                                    {
                                                                        item.genre.split(',').map((platf, idx) => {
                                                                            return (
                                                                                <Tag key={idx} color="blue">{platf}</Tag>
                                                                            )
                                                                        })
                                                                    }
                                                                    <hr />
                                                                    {
                                                                        item.platform.split(',').map((platf, idx) => {
                                                                            return (
                                                                                <Tag key={idx} color="success">{platf}</Tag>
                                                                            )
                                                                        })
                                                                    }
                                                                </>
                                                            )} />
                                                        </Card>
                                                    </Badge.Ribbon>
                                                </Badge.Ribbon>
                                            </NavLink>
                                        </List.Item>
                                    </>
                                )
                                }
                            />
                        </>
                    )
                }

                {
                    gameList === null && (<div style={{ textAlign: 'center' }}>Belum ada data tersedia ...</div>)
                }
            </>
        )
    }
}

export default Home