import React, { Component } from 'react'
import { List, Card, Badge } from 'antd'
import {
    StarOutlined,
    CalendarOutlined,
    ReadOutlined
} from '@ant-design/icons';
import axios from 'axios'
import { API_URL } from '../Config'
import { Image, Button, Typography } from 'antd';
import { NavLink } from 'react-router-dom';

const { Title } = Typography

class Movies extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movieList: null,
        }
    }

    componentDidMount() {
        this.state.movieList === null && this.getMovieList()
    }

    getMovieList() {
        console.log('get movie list')
        axios.get(`${API_URL}/data-movie`)
            .then((res) => {
                let mdata = res.data
                mdata = mdata.sort(function (x, y) {
                    return y.rating - y.rating
                })
                this.setState({
                    movieList: mdata,
                })
            }).catch((err) => {
                alert(`Maaf, kendala koneksi (Err: ${err})`)
            })
    }

    excerpt(str, length = 100) {
        return str.substring(0, length)
    }

    render() {
        let movieList = this.state.movieList
        const { Meta } = Card
        return (
            <>
                {
                    movieList !== null && (
                        <>
                            <Title level={2}>Daftar Film Film Terbaik</Title>
                            <List
                                grid={{
                                    gutter: 16,
                                    xs: 1,
                                    sm: 2,
                                    md: 4,
                                    lg: 4,
                                    xl: 4,
                                    xxl: 3,
                                }}
                                dataSource={movieList}
                                renderItem={(item, index) => (
                                    <>
                                        <List.Item key={index}>
                                            {/* {
                                        let duration_h = Math.floor(item.duration / 60);
                                        let duration_m = (item.duration % 60) === 0 ? '' : (item.duration % 60 + ' menit');
                                        let duration = `${duration_h} jam ${duration_m}`;
                                    } */}
                                            {/* <Badge.Ribbon text={(<Rate disabled defaultValue={item.rating / 2} />)}> */}
                                            <NavLink to={`/movie/${item.id}/${item.title}`}>
                                                <Badge.Ribbon placement="start" color="red" text={(<><CalendarOutlined /> <b>{item.year}</b></>)}>
                                                    <Badge.Ribbon placement="end" color="gold" text={(<><StarOutlined /> <b>{item.rating}</b></>)}>
                                                        <Card hoverable cover={
                                                            <Image alt={item.title} src={item.image_url}
                                                                fallback="data:image/png;base64,"
                                                            />
                                                        }>
                                                            <Meta title={item.title} description={this.excerpt(item.description)} />
                                                            <br />
                                                            <Button icon={<ReadOutlined />} type="primary">Read More ...</Button>
                                                        </Card>
                                                    </Badge.Ribbon>
                                                </Badge.Ribbon>
                                            </NavLink>
                                        </List.Item>
                                    </>
                                )
                                }
                            />
                        </>
                    )
                }
                <section style={{ display: 'none' }}>
                    {
                        movieList === null && (<div style={{ textAlign: 'center' }}>Belum ada data tersedia ...</div>)
                    }
                </section>
            </>
        )
    }
}

export default Movies